import React, { useState } from "react";
const App: React.FC = () => {
  interface Todo {
    title: string;
    text: string;
  }
  const [todo, setTodo] = useState<Todo[]>([]);
  const addTodo = (e: React.FormEvent) => {
    if (!(e instanceof HTMLInputElement)) {
      return;
    } else {
      const value = e.value;
      e.preventDefault();
      setTodo(value);
    }
  };
  return (
    <div>
      {todo}
      <input onChange={addTodo} />
    </div>
  );
};

export default App;
